import React from "react";
import styled from "styled-components";
import Button from "./Button";
import Block from "./Block";

const StyledPopup = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: rgba(0, 0, 0, 0.15);
  backdrop-filter: blur(6px);
  z-index: 1;
`;

const Background = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
`;

const Container = styled.div`
  position: relative;
  width: 600px;
  padding: 4rem;
  font-size: 1.4rem;
  font-weight: 500;
  color: var(--plain-dark);

  @media (max-width: 768px) {
    width: 90vw;
    padding: 2rem;
  }
`;

const Content = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  max-height: calc(90vh - 8rem);
  overflow-y: auto;
`;

const Header = styled.h3`
  font-size: 5.3rem;
  font-weight: 700;
  margin-bottom: 3rem;
  color: var(--main);
  transform: rotate(0deg);
`;

const Image = styled.img`
  width: 20rem;
  margin: 1rem 0 2rem 0;
`;

const Body = styled.p`
  font-size: 2.6rem;
  font-weight: 500;
  margin-bottom: 2rem;
  text-align: center;
  color: var(--sub);
`;

const ButtonContainer = styled.div`
  margin-top: 2rem;
  display: flex;

  button {
    margin: 0 1rem;
  }

  @media (max-width: 768px) {
    flex-direction: column;

    button {
      margin: 1rem 0;
    }
  }
`;

type Props = {
  show: boolean;
  close: () => void;
  header?: string;
  body?: string;
  content?: JSX.Element;
  image?: string;
  buttonText?: string;
  buttonAction?: () => void;
  buttonDisabled?: boolean;
  secondButtonText?: string;
  secondButtonAction?: () => void;
  loading?: boolean;
};

const Popup: React.FC<Props> = (props) => {
  if (!props.show) return null;

  return (
    <StyledPopup>
      <Background
        onClick={() => {
          if (props.loading) return;
          props.close();
        }}
      />
      <Container>
        <Block />
        <Content>
          {props.header && <Header>{props.header}</Header>}
          {props.image && <Image src={props.image} alt="Popup Image" />}
          {props.body && <Body>{props.body}</Body>}
          {props.content && props.content}
          {(props.buttonText || props.secondButtonText) && (
            <ButtonContainer>
              {props.buttonText && (
                <Button
                  disabled={props.buttonDisabled}
                  text={props.loading ? "Loading..." : props.buttonText}
                  click={() => {
                    if (props.loading) return;
                    if (props.buttonAction) props.buttonAction();
                  }}
                />
              )}
              {props.secondButtonText && (
                <Button
                  text={props.loading ? "Loading..." : props.secondButtonText}
                  click={() => {
                    if (props.loading) return;
                    if (props.secondButtonAction) props.secondButtonAction();
                  }}
                />
              )}
            </ButtonContainer>
          )}
        </Content>
      </Container>
    </StyledPopup>
  );
};

export default Popup;
