import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import styled from "styled-components";
import { getAddress } from "../services/contracthelper";
import { getTokens } from "../services/tokenService";
import { setTokens } from "../state/tokenSlice";
import { selectAddress, setAddress } from "../state/userSlice";
import Button from "./Button";
import CreateCapsule from "./CreateCapsule";
import FloatingCapsule from "./FloatingCapsule";

const StyledLanding = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  padding-bottom: 7rem;
`;

const Header = styled.h1`
  font-size: 17rem;
  font-weight: 700;
  color: var(--main);
  transform: rotate(0deg) translateY(0rem);
  height: 13rem;
  margin-left: 10rem;

  @media (max-width: 600px) {
    font-size: 7rem;
    margin-left: 0;
    text-align: center;
    transform: rotate(0deg) translateY(0);
    margin-top: 3rem;
  }
`;

const Content = styled.div`
  display: flex;
  justify-content: space-evenly;
  align-items: center;

  @media (max-width: 600px) {
    flex-direction: column-reverse;
  }
`;

const Left = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 75rem;
  margin-left: 4rem;
  margin-top: 7rem;
  transform: rotate(0deg);

  @media (max-width: 600px) {
    width: 100%;
    margin-top: 0;
    margin-left: 0;
  }
`;

const SubHeader = styled.div`
  color: var(--sub);
  margin-top: 25px;
  font-size: 3.3rem;
  width: 75%;
  line-height: 1.4;
  margin-bottom: 7rem;
  margin-left: 10rem;

  @media (max-width: 600px) {
    margin-top: 0;
    margin-left: 0;
    width: 90%;
    margin-left: 2rem;
    margin-bottom: 6rem;
  }
`;

const SubHeaderSpan = styled.span`
  color: var(--sub);
  margin-top: 25px;
  font-size: 2rem;
  width: 75%;
  line-height: 1.4;
  margin-bottom: 7rem;
  margin-left: 10rem;

  @media (max-width: 600px) {
    margin-top: 0;
    margin-left: 0;
    width: 90%;
    margin-left: 2rem;
    margin-bottom: 6rem;
  }
`;

const ButtonContainer = styled.div`
  margin-bottom: 13rem;
  transform: rotate(0deg);

  @media (max-width: 600px) {
    margin-bottom: 3rem;
  }
`;

const Landing = (): JSX.Element => {
  const dispatch = useDispatch();
  const address = useSelector(selectAddress);

  const [creatingCapsule, setCreatingCapsule] = useState(false);

  const connect = async () => {
    const _address = await getAddress();
    dispatch(setAddress(_address));
    const tokens = await getTokens();
    dispatch(setTokens(tokens));
  };

  return (
    <StyledLanding>
      <Header>Time Vault</Header>
      <Content>
        <Left>
          <SubHeader>
            "Be a friend of time."
            <SubHeaderSpan>
              <br />A BlockChain Lecture Final Project: Time Vault
              <br />
              Team Member: LI Bosong, PENG Feng, HOU Zhizhong, CHEN Lingfei,
              FENG Zhe
            </SubHeaderSpan>
          </SubHeader>
          <ButtonContainer>
            <Button
              primary
              text={address ? "Create Vault" : "Connect"}
              click={() => {
                if (address) setCreatingCapsule(true);
                else connect();
              }}
            />
          </ButtonContainer>
        </Left>
        <FloatingCapsule />
      </Content>
      <CreateCapsule
        show={creatingCapsule}
        close={() => setCreatingCapsule(false)}
      />
    </StyledLanding>
  );
};

export default Landing;
